sys_variable_value.value:
(function execute(inputs, outputs) {

  	if (inputs.operation == 'add')
		outputs.new_value = parseFloat(inputs.original_value) + parseFloat(inputs.adjustment);
  	else
      	outputs.new_value = parseFloat(inputs.original_value) - parseFloat(inputs.adjustment);


})(inputs, outputs);

