sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var grPlan = new GlideRecord('x_656683_oluhome_payment_plan');
    var schBalance, paymentAmount, newSchBalance;

    // If adding new payment, subtract the amount from scheduled balance
	// If deleting a payment, add the amount back to the scheduled balance
	// If updating the amount of a payment, add back the original amount and then subtract the new amount
    if (current.operation() == 'insert') {
        grPlan.get(current.getValue('payment_plan'));
        schBalance = parseFloat(grPlan.getValue('scheduled_balance'));
        paymentAmount = parseFloat(current.getValue('payment_amount'));
        newSchBalance = schBalance - paymentAmount;
        grPlan.setValue('scheduled_balance', newSchBalance);
        grPlan.update();
    }
	else if (current.operation() == 'delete') {
        grPlan.get(current.getValue('payment_plan'));
        schBalance = parseFloat(grPlan.getValue('scheduled_balance'));
        paymentAmount = parseFloat(current.getValue('payment_amount'));
        newSchBalance = schBalance + paymentAmount;
        grPlan.setValue('scheduled_balance', newSchBalance);
        grPlan.update();
    }

})(current, previous);
