sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Fields for Completeness
	// ===========================
	// Short Description
	// Photo
	// Notes
	// Seller
	// Purchase Price
	// Purchase Date
	// Ticket Price
	// Primary Color
	// Dimensions
	// Origin
	// Time period
	// Style

	var fieldList = [
		'short_description',
		'photo',
		'notes',
		'seller',
		'purchase_price',
		'purchase_date',
		'ticket_price',
		'primary_color',
		'length',
		'width',
		'height',
		'origin',
		'time_period',
		'style'
	];

	var totalFields = fieldList.length;
	var completeFields = 0;

	for (var i = 0; i < fieldList.length; i++) {
		var currentField = current.getValue(fieldList[i]);
		if (currentField) {
			if (currentField != '' || currentField > 0) {
				completeFields += 1;
			}
		}
	}

	var completeness = completeFields / totalFields * 100.00;
	current.setValue('record_completeness', completeness);

})(current, previous);
